import numpy as np
import matplotlib.pyplot as plt
import matplotlib.ticker as plticker
import os

from read2D import read2D
from Dyson import Dyson
from fit2exponential import fit2exponential


import warnings
warnings.filterwarnings(action="ignore", module="matplotlib", message="^tight_layout")

def calculateEandZ (files, plot = False):

  Es = []
  Zs = []
  Ns = []

  for i, file in enumerate(files):

    t, S, parameters = read2D(file, 0)

    if len(t) > 0:

      p = 0.01
      a = parameters['a']
      mu = parameters['mu']
      N = parameters['N']

      G = Dyson(S, t, a, p, mu)
      E, Z = fit2exponential(G, t, p, mu)

      Es += [E]
      Zs += [Z]
      Ns += [N]


  avgE = 0
  stdE = 0
  Es = np.array([Es])
  Zs = np.array([Zs])
  Ns = np.array([Ns])

  if Es.size > 0:

    avgE = np.average(Es, weights=Ns)
    stdE = np.sqrt(np.average((Es - avgE)**2, weights=Ns))

    avgZ = np.average(Zs, weights=Ns)
    stdZ = np.sqrt(np.average((Zs - avgZ)**2, weights=Ns))

    # print(avgE, stdE, Es, np.round(Ns/np.sum(Ns), 4))

    if plot:

      plt.errorbar(0, avgE, yerr=stdE, color='red', marker='x', ls="none")
      plt.scatter(np.array(Es)*0, Es, s=300*Ns/np.sum(Ns))
      plt.show()


    return avgE, stdE, avgZ, stdZ
  else:
    return False, False, False, False















